import React from 'react'
import { Card, Table } from 'antd'
import axios from 'axios'

export default class BasicTable extends React.Component {

  state = {
    dataSource: [],
    dataSource2: []
  }

  componentDidMount() {
    const dataSource = [
      {
        id: '0',
        userName: 'jack',
        sex: '1',
        state: '1',
        interests: '1',
        birthday: '2000-01-01',
        address: '支付宝大楼'
      },{
        id: '1',
        userName: 'jackMa',
        sex: '1',
        state: '1',
        interests: '1',
        birthday: '2000-01-01',
        address: '支付宝大楼'
      },{
        id: '2',
        userName: 'jackChen',
        sex: '1',
        state: '1',
        interests: '1',
        birthday: '2000-01-01',
        address: '支付宝大楼'
      }
    ]
    this.requestTableData()
    this.setState({
      dataSource
    })
  }

  // 动态获取mock数据
  requestTableData = () => {
    let baseUrl = 'https://www.easy-mock.com/mock/5d6008c4166aea330e4cfcd3/mockapi'
    axios.get(baseUrl + '/table/list').then((res) => {
      let data = res.
      console.log(res)
    })
  }

  render() {
    const columns = [
      {
        title: 'id', // 对应的列名
        dataIndex: 'id', // 数据源属性值 需要渲染的字段
      },
      {
        title: '用户名',
        dataIndex: 'userName'
      },
      {
        title: '性别',
        dataIndex: 'sex'
      },
      {
        title: '状态',
        dataIndex: 'state'
      },
      {
        title: '爱好',
        dataIndex: 'interest'
      },
      {
        title: '生日',
        dataIndex: 'birthday'
      },
      {
        title: '地址',
        dataIndex: 'address'
      }
    ]
    return (
      <div>
        <Card title='基础表格'>
          <Table
            columns={columns}
            dataSource={this.state.dataSource}
            bordered
            pagination={false}
          />

        </Card>
        <Card title='动态数据渲染表格' style={{marginTop: 20}}>
          <Table
            columns={columns}
            dataSource={this.state.dataSource2}
            bordered
            pagination={false}
          />

        </Card>
      </div>
    )
  }
}
