import React from 'react'
import { Modal, Card, Table, Button, message } from 'antd'
import axios from '../../axios/index'

export default class BasicTable extends React.Component {

  state = {
    dataSource: [],
    dataSource2: [],
    selectRowKeys: [],
    selectedIds: [],
    selectedItem: ''
  }

  componentDidMount() {
    let dataSource = [
      {
        id: '0',
        userName: 'jack',
        sex: '1',
        state: '1',
        interests: '1',
        birthday: '2000-01-01',
        address: 'ÊîØ‰ªòÂÆùÂ§ßÊ•º'
      },{
        id: '1',
        userName: 'jackMa',
        sex: '1',
        state: '1',
        interests: '1',
        birthday: '2000-01-01',
        address: 'ÊîØ‰ªòÂÆùÂ§ßÊ•º'
      },{
        id: '2',
        userName: 'jackChen',
        sex: '1',
        state: '1',
        interests: '1',
        birthday: '2000-01-01',
        address: 'ÊîØ‰ªòÂÆùÂ§ßÊ•º'
      }
    ]
    dataSource.map((item, index) => {
      return item.key = index
    })
    this.requestTableData()
    this.setState({
      dataSource
    })
  }

  // Âä®ÊÄÅËé∑ÂèñmockÊï∞ÊçÆ
  requestTableData = () => {
    axios.ajax({
      url: '/table/list',
      data: {
        params: {
          page: 1
        },
      }
    }).then((res) => {
      if (res.code === 0) {
        res.result.map((item, index) => {
          return item.key === index
        })
        this.setState({
          dataSource2: res.result
        })
      }
    })
  }

  // Áî®Êà∑ÁÇπÂáªÊØè‰∏ÄË°å
  onRowClick = (record, index) => {
    let selectKey = [index]; // ‰∏∫‰ªÄ‰πàÊòØÊï∞ÁªÑ Âõ†‰∏∫ÊúâÂèØËÉΩÊòØÂ§öÈÄâÁöÑ
    console.log('here console')
    console.log(selectKey)
    console.log(record)
    this.setState({
      selectedRowKeys: selectKey, // ÁÑ∂ÂêéÊääÂΩìÂâçÊâÄ‰∏≠ÁöÑkeyÂÄºÂíåÂΩìÂâçÈÄâ‰∏≠ÁöÑÂÜÖÂÆπÈ°π‰øùÂ≠òÂà∞state‰∏≠
      selectedItem: record
    })
  }

  // Âà†Èô§ÊåâÈíÆÊèêÁ§∫
  handleDelete = () => {
    const {selectedIds, selectedItem} = this.state
    Modal.warning({
      title: 'Âà†Èô§',
      content: `‰Ω†Á°ÆÂÆöË¶ÅÂà†Èô§‰πà${selectedItem.id}`,
      onOk: () => {
        message.success('Âà†Èô§ÊàêÂäü')
      }
    })
  }

  render() {
    const columns = [
      {
        title: 'id', // ÂØπÂ∫îÁöÑÂàóÂêç
        dataIndex: 'id', // Êï∞ÊçÆÊ∫êÂ±ûÊÄßÂÄº ÈúÄË¶ÅÊ∏≤ÊüìÁöÑÂ≠óÊÆµ
      },
      {
        title: 'Áî®Êà∑Âêç',
        dataIndex: 'userName'
      },
      {
        title: 'ÊÄßÂà´',
        dataIndex: 'sex',
        render(sex) { // render ÈªòËÆ§Êé•Êî∂Âà∞dataIndexÂ≠óÊÆµÈáåÈù¢Âà∞ÂÄº
          return sex === 1 ? 'Áî∑' : 'Â•≥'
        }
      },
      {
        title: 'Áä∂ÊÄÅ',
        dataIndex: 'state',
        render(state) {
          let config = {
            '1': 'hello',
            '2': 'world',
            '3': 'nihao',
            '4': '‰Ω†Âè∑‰πà',
            '5': 'sha diao'
          }
          return config[state]
        }
      },
      {
        title: 'Áà±Â•Ω',
        dataIndex: 'interests',
        render(state) {
          let config = {
            '1': 'Ê∏∏Ê≥≥',
            '2': 'ÂÅ•Ë∫´',
            '3': 'üèÄ',
            '4': 'Ë∑ëÊ≠•',
            '5': 'Ê°åÁêÉ',
            '6': 'Áà¨Â±±',
            '7': 'ËóèËóè‰∏™',
            '8': '‰Ω†Â•Ω'
          }
          return config[state]
        }
      },
      {
        title: 'ÁîüÊó•',
        dataIndex: 'birthday'
      },
      {
        title: 'Âú∞ÂùÄ',
        dataIndex: 'address'
      }
    ]

    const rowSelection = {
      type: 'radio',
      selectedRowKeys: this.state.selectedRowKeys,
      onChange: (key, row) => {
        console.log(key)
        console.log(row)
      }
    }

    const rowCheckSelection = {
      type: 'checkbox',
      selectedRowKeys: this.state.selectedRowKeys,
      onChange: (selectedRowKeys, selectedRows) => {
        let ids = []
        selectedRows.map(item => {
          ids.push(item.id)
        })
      //  const selectedIds = selectedRows.map(item => {
      //    return item.id
      //  })
        console.log('selectedRows')
        console.log(selectedRows)
        console.log('record')
        console.log(this.state.selectedItem)
        this.setState({
          selectedRowKeys, // Ëøô‰∏™ÊòØÂøÖÈ°ªÁöÑ
          selectedIds: ids // ÈôÑÂä†ÁöÑ
        })
      }
    }
    return (
      <div>
        <Card title='Âü∫Á°ÄË°®Ê†º'>
          <Table
            columns={columns}
            dataSource={this.state.dataSource}
            bordered
            pagination={false}
          />

        </Card>
        <Card title='Âä®ÊÄÅÊï∞ÊçÆÊ∏≤ÊüìË°®Ê†º - Mock' style={{marginTop: 20}}>
          <Table
            columns={columns}
            dataSource={this.state.dataSource2}
            bordered
            pagination={false}
          />

        </Card>

        <Card title='ÂçïÈÄâË°®Ê†º - Mock' style={{marginTop: 20}}>
          <Table
            columns={columns}
            rowSelection={rowSelection} // type(ÂçïÈÄâÊàñËÄÖÂ§öÈÄâ); onSelect(ÈÄâ‰∏≠‰πãÂêéÂà∞ÂõûË∞É); onChange(ÈÄâ‰∏≠ÁöÑÂàáÊç¢ÂõûË∞É)
            dataSource={this.state.dataSource2}
            bordered
            pagination={false}
            onRow={(record, index) => {
              return {
                onClick: () => { // row ÂΩìÂâçË°åË¢´ÁÇπÂáª‰πãÂêéÁöÑÂõûË∞É
                  this.onRowClick(record, index)
                }
              }
            }}
          />

        </Card>
        <Card title='Â§öÈÄâË°®Ê†º - Mock' style={{marginTop: 20}}>
          <div>
            <Button onClick={this.handleDelete} type='primary'>Âà†Èô§</Button>
          </div>
          <Table
            columns={columns}
            rowSelection={rowCheckSelection} // type(ÂçïÈÄâÊàñËÄÖÂ§öÈÄâ); onSelect(ÈÄâ‰∏≠‰πãÂêéÂà∞ÂõûË∞É); onChange(ÈÄâ‰∏≠ÁöÑÂàáÊç¢ÂõûË∞É)
            dataSource={this.state.dataSource2}
            bordered
            pagination={false}
            onRow={(record, index) => {
              return {
                onClick: () => { // row ÂΩìÂâçË°åË¢´ÁÇπÂáª‰πãÂêéÁöÑÂõûË∞É
                  this.onRowClick(record, index)
                }
              }
            }}
          />

        </Card>
      </div>
    )
  }
}
