import React from 'react'
import {DatePicker, Button, Card, Form, Select, Table} from 'antd'
import Utils from "../../utils/utils"
import axios from '../../axios'

const FormItem = Form.Item
const Option = Select.Option

class Order extends React.Component {

  state = {}

  componentDidMount() {
    this.requestList()
  }

  requestList = () => {

  }

  render() {
    const columns = [
      {
        title: '订单编号',
        dataIndex: 'order_sn'
      },
      {
        title: '车辆编号',
        dataIndex: 'bike_sn'
      },
      {
        title: '用户名',
        dataIndex: 'user_name',
      },
      {
        title: '手机号',
        dataIndex: 'mobile',
      },
      {
        title: '里程',
        dataIndex: 'distance'
      },
      {
        title: '行驶时长',
        dataIndex: 'total_time',
      },
      {
        title: '状态',
        dataIndex: 'status',
      },
      {
        title: '开始时间',
        dataIndex: 'start_time',
      },
      {
        title: '结束时间',
        dataIndex: 'end_time'
      },
      {
        title: '订单金额',
        dataIndex: 'total_fee'
      },
      {
        title: '实付金额',
        dataIndex: 'user_pay'
      }
      ]
    return (
      <div>
        <Card>
          <FilterForms />
        </Card>
        <Card>
          <Button>订单详情</Button>
          <Button>结束订单</Button>
        </Card>
        <div className='content-wrap'>
          <Table
            bordered
            columns={columns}
            dataSource={this.state.list}
            pagination={this.state.pagination}
          />
        </div>
      </div>
    )
  }
}

export default Order

class FilterForm extends React.Component {
  render() {
    const {getFieldDecorator} = this.props.form
    return (
      <div>
        <Form layout="inline">
          <FormItem width={200} label='订单状态'>
            {
              getFieldDecorator('state', {
                initialValue: ''
              })(
                <Select>
                  <Option value=''> 全部 </Option>
                  <Option value='1'> 进行中 </Option>
                  <Option value='2'> 结束行程 </Option>
                </Select>
              )
            }
          </FormItem>
          <FormItem width={200} label='订单时间'>
            {
              getFieldDecorator('start_time', {
                initialValue: '2'
              })(
                <DatePicker showTime format='YYYY-MM-DD HH:mm:ss'/>
              )
            }
          </FormItem>
          <FormItem width={200} label='订单结束时间'>
            {
              getFieldDecorator('end_time', {
                initialValue: ''
              })(
                <DatePicker showTime format='YYYY-MM-DD HH:mm:ss'/>
              )
            }
          </FormItem>
          <FormItem>
            <Button type='primary'>查询</Button>
            <Button>重置</Button>
          </FormItem>
        </Form>
      </div>
    )
  }
}

const FilterForms = Form.create()(FilterForm)
