import React from 'react'
import { Card, Table } from 'antd'
import axios from '../../axios/index'

export default class BasicTable extends React.Component {

  state = {
    dataSource: [],
    dataSource2: []
  }

  componentDidMount() {
    let dataSource = [
      {
        id: '0',
        userName: 'jack',
        sex: '1',
        state: '1',
        interests: '1',
        birthday: '2000-01-01',
        address: '支付宝大楼'
      },{
        id: '1',
        userName: 'jackMa',
        sex: '1',
        state: '1',
        interests: '1',
        birthday: '2000-01-01',
        address: '支付宝大楼'
      },{
        id: '2',
        userName: 'jackChen',
        sex: '1',
        state: '1',
        interests: '1',
        birthday: '2000-01-01',
        address: '支付宝大楼'
      }
    ]
    dataSource.map((item, index) => {
      return item.key = index
    })
    this.requestTableData()
    this.setState({
      dataSource
    })
  }

  // 动态获取mock数据
  requestTableData = () => {
    axios.ajax({
      url: '/table/list',
      data: {
        params: {
          page: 1
        },
      }
    }).then((res) => {
      if (res.code === 0) {
        res.result.map((item, index) => {
          return item.key === index
        })
        this.setState({
          dataSource2: res.result
        })
      }
    })
  }

  render() {
    const columns = [
      {
        title: 'id', // 对应的列名
        dataIndex: 'id', // 数据源属性值 需要渲染的字段
      },
      {
        title: '用户名',
        dataIndex: 'userName'
      },
      {
        title: '性别',
        dataIndex: 'sex',
        render(sex) { // render 默认接收到dataIndex字段里面到值
          return sex === 1 ? '男' : '女'
        }
      },
      {
        title: '状态',
        dataIndex: 'state',
        render(state) {
          let config = {
            '1': 'hello',
            '2': 'world',
            '3': 'nihao',
            '4': '你号么',
            '5': 'sha diao'
          }
          return config[state]
        }
      },
      {
        title: '爱好',
        dataIndex: 'interests',
        render(state) {
          let config = {
            '1': '游泳',
            '2': '健身',
            '3': '🏀',
            '4': '跑步',
            '5': '桌球',
            '6': '爬山',
            '7': '藏藏个',
            '8': '你好'
          }
          return config[state]
        }
      },
      {
        title: '生日',
        dataIndex: 'birthday'
      },
      {
        title: '地址',
        dataIndex: 'address'
      }
    ]

    const rowSelection = {
      type: 'radio'
    }
    return (
      <div>
        <Card title='基础表格'>
          <Table
            columns={columns}
            dataSource={this.state.dataSource}
            bordered
            pagination={false}
          />

        </Card>
        <Card title='动态数据渲染表格 - Mock' style={{marginTop: 20}}>
          <Table
            columns={columns}
            dataSource={this.state.dataSource2}
            bordered
            pagination={false}
          />

        </Card>

        <Card title='单选表格 - Mock' style={{marginTop: 20}}>
          <Table
            columns={columns}
            rowSelection={rowSelection} // 
            dataSource={this.state.dataSource2}
            bordered
            pagination={false}
            onRow={(record) => {
              return {
                onClick: () => {

                }
              }
            }}
          />

        </Card>
      </div>
    )
  }
}
