import React from 'react'
import { Modal, Card, Table, Button, message } from 'antd'
import axios from '../../axios/index'
import Utils from '../../utils/utils'

export default class HighTable extends React.Component {

  state = {
    dataSource: [],
    pagination: {},
    selectedRowKeys: [],
    selectedItem: [],
    sortOrder: '',
    selectedRows: [],
  }

  params = {
    currentPage: 1
  }

  // 调用请求的方法
  componentDidMount() {
    this.request()
  }

  // 表格change事件触发
  handleTableChange = (pagination, filters, sorter) => {
    console.log('sorter')
    console.log(sorter)
    this.setState({
      sortOrder: sorter.order
    })
  }

  // 点击表格单行数据
  onRowClick = (record, index) => {
    let selectKey = [index]; // 为什么是数组 因为有可能是多选的
    console.log('here console')
    console.log(selectKey)
    console.log(record)
    this.setState({
      selectedRowKeys: selectKey, // 然后把当前所中的key值和当前选中的内容项保存到state中
      selectedItem: record
    })
  }

  // 请求mock数据
  request = () => {
    let _this = this
    axios.ajax({
      url: '/table/highTable',
      data: {
        params: {
          page: _this.params.page
        }
      }
    }).then((res) => {
      if (res.code === 0) {
        res.result.list.map((item, index) => {
          return item.key === index
        })
        this.setState({
          dataSource: res.result.list,
          pagination: Utils.pagination(res, (current) => {
            // TODO 获取当前页面 传给接口
            _this.params.page = current
            this.request()
          })
        })
      }
    })
  }

  render() {
    const columns = [
      {
        title: 'id', // 对应的列名
        dataIndex: 'id', // 数据源属性值 需要渲染的字段
        width: 80
      },
      {
        title: '用户名',
        dataIndex: 'userName',
        width: 80
      },
      {
        title: '性别',
        dataIndex: 'sex',
        render(sex) { // render 默认接收到dataIndex字段里面到值
          return sex === 1 ? '男' : '女'
        },
        width: 80
      },
      {
        title: '状态',
        dataIndex: 'state',
        render(state) {
          let config = {
            '1': 'hello',
            '2': 'world',
            '3': 'nihao',
            '4': '你号么',
            '5': 'sha diao'
          }
          return config[state]
        },
        width: 80
      },
      {
        title: '爱好',
        dataIndex: 'interests',
        render(state) {
          let config = {
            '1': '游泳',
            '2': '健身',
            '3': '🏀',
            '4': '跑步',
            '5': '桌球',
            '6': '爬山',
            '7': '藏藏个',
            '8': '你好'
          }
          return config[state]
        },
        width: 80
      },
      {
        title: '生日',
        dataIndex: 'birthday',
        width: 120
      },
      {
        title: '地址',
        dataIndex: 'address',
        width: 120
      }
    ]
    const columns2 = [
      {
        title: 'id', // 对应的列名
        dataIndex: 'id', // 数据源属性值 需要渲染的字段
        width: 80
      },
      {
        title: '用户名',
        dataIndex: 'userName',
        width: 80
      },
      {
        title: '年龄',
        dataIndex: 'age',
        sorter: (a, b) => { // a, b 是列的字段 通过a.age 可以获得a中的age值
          return a.age - b.age
        },
        sortOrder: this.state.sortOrder,
        width: 80
      },
      {
        title: '性别',
        dataIndex: 'sex',
        render(sex) { // render 默认接收到dataIndex字段里面到值
          return sex === 1 ? '男' : '女'
        },
        width: 80
      },
      {
        title: '状态',
        dataIndex: 'state',
        render(state) {
          let config = {
            '1': 'hello',
            '2': 'world',
            '3': 'nihao',
            '4': '你号么',
            '5': 'sha diao'
          }
          return config[state]
        },
        width: 80
      },
      {
        title: '爱好',
        dataIndex: 'interests',
        render(state) {
          let config = {
            '1': '游泳',
            '2': '健身',
            '3': '🏀',
            '4': '跑步',
            '5': '桌球',
            '6': '爬山',
            '7': '藏藏个',
            '8': '你好'
          }
          return config[state]
        },
        width: 80
      },
      {
        title: '生日',
        dataIndex: 'birthday',
        width: 120
      },
      {
        title: '地址',
        dataIndex: 'address',
        width: 120
      }
    ]

    const rowCheckSelection = {
      type: 'checkbox',
      selectedRowKeys: this.state.selectedRowKeys,
      onChange: (selectedRowKeys, selectedRows) => {
        let ids = []
        selectedRows.map(item => {
          ids.push(item.id)
        })
        //  const selectedIds = selectedRows.map(item => {
        //    return item.id
        //  })
        console.log('selectedRows')
        console.log(selectedRows)
        console.log('record')
        console.log(this.state.selectedItem)
        this.setState({
          selectedRowKeys, // 这个是必须的
          selectedIds: ids, // 附加的
          selectedRows: selectedRows // 多选时 把选中的所有数据存起来 方便管理 onRow 会存单条数据
        })
      }
    }

    return (
      <div>
        <Card title='头部固定' style={{marginTop: 20}}>
          <Table
            columns={columns}
            rowSelection={rowCheckSelection} // type(单选或者多选); onSelect(选中之后到回调); onChange(选中的切换回调)
            dataSource={this.state.dataSource}
            bordered
            scroll={{y: 240}}
            pagination={false}
            onRow={(record, index) => {
              return {
                onClick: () => { // row 当前行被点击之后的回调
                  this.onRowClick(record, index)
                }
              }
            }}
          />

        </Card>
        <Card title='左侧固定' style={{marginTop: 20}}>
          <Table
            columns={columns}
            rowSelection={rowCheckSelection} // type(单选或者多选); onSelect(选中之后到回调); onChange(选中的切换回调)
            dataSource={this.state.dataSource}
            bordered
            pagination={false}
            onRow={(record, index) => {
              return {
                onClick: () => { // row 当前行被点击之后的回调
                  this.onRowClick(record, index)
                }
              }
            }}
          />

        </Card>
        <Card title='表格排序' style={{marginTop: 20}}>
          <Table
            columns={columns2}
            rowSelection={rowCheckSelection} // type(单选或者多选); onSelect(选中之后到回调); onChange(选中的切换回调)
            dataSource={this.state.dataSource}
            bordered
            pagination={false}
            onRow={(record, index) => {
              return {
                onClick: () => { // row 当前行被点击之后的回调
                  this.onRowClick(record, index)
                }
              }
            }}
            onChange={this.handleTableChange} // 分页、排序、 筛选时发生变化的回调
          />

        </Card>
      </div>
    )
  }
}
